mapper.select:
SELECT ID, ART_TITLE, ART_CATEGORY, ART_MATERIAL, ART_SIZE, ART_DESCRIPTION, ART_END_DATE, USER_ID
FROM TBL_ART
WHERE ID = #{id}

mapper.update:
UPDATE TBL_ART
SET ART_TITLE = #{artTitle}, ART_CATEGORY = #{artCategory}, ART_MATERIAL = #{artMaterial},
    ART_SIZE = #{artSize}, ART_DESCRIPTION = #{artDescription}, ART_END_DATE = #{artEndDate}
WHERE ID = #{id}

UPDATE TBL_ART
SET ART_STATUS = #{artStatus}
WHERE ID = #{id}
