AUCTION_ATTRACTED = 1, USER_ID = #{userId}, AUCTION_BID_PRICE = #{auctionBidPrice}
AUCTION_ATTRACTED = 0
, AUCTION_BID_DATE = TO_DATE(#{auctionBidDate}, 'YYYY-MM-DD HH24:MI:SS')
WHERE ID = #{id}
