mapper.select:
SELECT COUNT(ID)
FROM TBL_AUCTION
where ART_ID = #{artId}

mapper.update:
UPDATE TBL_AUCTION SET AUCTION_START_DATE = TO_DATE(#{auctionStartDate}, 'YYYY-MM-DD HH24:MI:SS'), AUCTION_END_DATE = TO_DATE(#{auctionEndDate}, 'YYYY-MM-DD HH24:MI:SS'), AUCTION_START_PRICE = #{auctionStartPrice}, AUCTION_ESTIMATED_MIN_PRICE = #{auctionEstimatedMinPrice}, AUCTION_ESTIMATED_MAX_PRICE = #{auctionEstimatedMaxPrice},
AUCTION_ATTRACTED = 1, USER_ID = #{userId}, AUCTION_BID_PRICE = #{auctionBidPrice}
AUCTION_ATTRACTED = 0
, AUCTION_BID_DATE = TO_DATE(#{auctionBidDate}, 'YYYY-MM-DD HH24:MI:SS')
WHERE ID = #{id}
