mapper.select:
SELECT ID, ART_POST_ID, USER_ID, COMMENT_CONTENT, COMMENT_DATE
FROM TBL_COMMENT
WHERE ID = #{id}

mapper.update:
UPDATE TBL_COMMENT
SET COMMENT_CONTENT = #{commentContent}, COMMENT_DATE = #{commentDate}
WHERE ID = #{id}

