mapper.select:
SELECT ID
FROM TBL_USER
WHERE USER_EMAIL = #{receiveUserEmail}

mapper.update:
UPDATE TBL_MAIL
SET MAIL_RECEIVE_USER_DELETED = 1
WHERE ID = #{id}
  AND RECEIVE_USER_ID = #{receiveUserId}

UPDATE TBL_MAIL
SET MAIL_SEND_USER_DELETED = 1
WHERE ID = #{id}
  AND SEND_USER_ID = #{sendUserId}

mapper.select:
SELECT ID, MAIL_TITLE, MAIL_CONTENT, MAIL_SEND_TIME
FROM TBL_MAIL
WHERE ID = #{id}
  AND RECEIVE_USER_ID = #{receiveUserId}

mapper.update:
UPDATE TBL_MAIL
SET MAIL_OPEN_OK = 1
WHERE ID = #{id}
AND RECEIVE_USER_ID = #{receiveUserId}
